package research

import (
	"fmt"
	"github.com/shopspring/decimal"
	"math/big"
	"testing"
)

func TestDecimal_Test(t *testing.T) {
	price := decimal.NewFromBigInt(big.NewInt(12345), 0)
	d := decimal.NewFromBigInt(big.NewInt(1), -18)
	fmt.Println(price.Mul(d))
	//compare
	k1, _ := decimal.NewFromString("0.")
	k2, _ := decimal.NewFromString("0.")
	fmt.Println(k1.Cmp(k2))
}
